pragma solidity ^0.8.20;

import {Token} from "./Token.sol";
import "./NativeLiquidityPool.sol";
import { OApp, Origin, MessagingFee } from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract Factory is OApp {
    uint256 public constant TARGET = 3 ether;
    uint256 public constant TOKEN_LIMIT = 500_000 ether;
    uint256 public constant BASE_REWARD_PERCENTAGE = 3; // 3% reward

    // Pack related variables together to save gas
    uint256 public immutable fee;
    uint256 public totalTokens;
    address public immutable lzEndpoint;
    NativeLiquidityPool public nativeLiquidityPool;

    address[] public tokens;
    //mapping(string => address) public tokenBySymbol;
    mapping(address => TokenSale) public tokenToSale;
    mapping(address => mapping(address => uint256)) public userTokenContributions;
    mapping(address => mapping(address => uint256)) public userEthContributions;
    mapping(address => mapping(address => bool)) public hasClaimedReward;
    mapping(address => address[]) public tokenContributors;

    struct TokenSale {
        address token;
        string name;
        string metadataURI;
        address creator;
        uint256 sold;
        uint256 raised;
        bool isOpen;
        bool isLiquidityCreated;
    }

    constructor(uint256 _fee, address _lzEndpoint) OApp(_lzEndpoint, msg.sender) Ownable(msg.sender) {
        fee = _fee;
        lzEndpoint = _lzEndpoint;
    }

    function setLiquidityPool(address _liquidityPool) external onlyOwner {
        nativeLiquidityPool = NativeLiquidityPool(_liquidityPool);
    }

    function create(
        string calldata _name, 
        string calldata _symbol,
        string calldata _metadataURI,
        address _creator
    ) external payable {
        require(msg.value >= fee, "!fee");
        //require(tokenBySymbol[_symbol] == address(0), "Token with this symbol already exists");
        // Default to msg.sender if _creator is zero address
        if (_creator == address(0)) {
            _creator = msg.sender;
        }

        Token token = new Token(_creator, _name, _symbol, _metadataURI, 1_000_000 ether, lzEndpoint);
        tokens.push(address(token));
        totalTokens++;

        TokenSale memory sale = TokenSale(address(token), _name, _metadataURI, _creator, 0, 0, true, false);
        tokenToSale[address(token)] = sale;
    }

    function buy(address _token, uint256 _amount) external payable {
        TokenSale storage sale = tokenToSale[_token];
        require(sale.token != address(0) && sale.isOpen, "!available");
        require(_amount >= 1 ether && _amount <= 10000 ether, "!amount");

        uint256 price = getCost(sale.sold) * (_amount / 1e18);
        require(msg.value >= price, "!price");

        // Record the contribution
        if (userTokenContributions[_token][msg.sender] == 0) {
            tokenContributors[_token].push(msg.sender); // Store unique contributors
        }
        userTokenContributions[_token][msg.sender] += _amount;
        userEthContributions[_token][msg.sender] += msg.value;

        // transfer tokens to the buyer's wallet
        Token(_token).transfer(msg.sender, _amount);

        // Update the sale
        sale.sold += _amount;
        sale.raised += msg.value;
        // Make sure fund raising goal isn't met, since if its met we don't want people to keep buy tokens
        if (sale.sold >= TOKEN_LIMIT || sale.raised >= TARGET){
            sale.isOpen = false;
            if (!sale.isLiquidityCreated){
                triggerLiquidityCreation(_token);
                sale.isLiquidityCreated = true;
            }
        }
    }

    function triggerLiquidityCreation(address _token) internal {
        TokenSale storage sale = tokenToSale[_token];
        require(!sale.isLiquidityCreated, "!liquidity"); 
        Token memeTokenCt = Token(_token);
        uint256 tokenBalance = memeTokenCt.balanceOf(address(this));

        (address[] memory contributors, uint256[] memory contributorAmounts) = getContributors(_token);

        memeTokenCt.approve(address(nativeLiquidityPool), tokenBalance);
        nativeLiquidityPool.addLiquidity{value: sale.raised}(_token, tokenBalance, contributors, contributorAmounts);
    }

    function calculateReward(address _token, address user) internal view returns (uint256) {
        if (!tokenToSale[_token].isLiquidityCreated || 
            hasClaimedReward[_token][user] ||
            userTokenContributions[_token][user] == 0) {
            return 0;
        }

        uint256 userTokens = userTokenContributions[_token][user];
        TokenSale storage sale = tokenToSale[_token];
        return (sale.raised * BASE_REWARD_PERCENTAGE * userTokens) / (sale.sold * 100);
    }

    function claimReward(address _token) public {
        require(tokenToSale[_token].isLiquidityCreated, "!liquidity");
        require(!hasClaimedReward[_token][msg.sender], "Already claimed!");
        require(userTokenContributions[_token][msg.sender] > 0, "!contribution");

        uint256 reward = calculateReward(_token, msg.sender);
        require(reward > 0, "!reward");

        hasClaimedReward[_token][msg.sender] = true; 
        Token(_token).mint(msg.sender, reward); // distribute token to LPs

    }

    function withdraw(uint256 _amount) external onlyOwner{
        (bool success, ) = payable(owner()).call{value: _amount}("");
        require(success, "!transfer");
    }

    function getTokenSale(uint256 _index) external view returns (TokenSale memory){
        require(_index < tokens.length, "!index");
        return tokenToSale[tokens[_index]];
    }

    function getCost(uint256 _sold) internal pure returns(uint256){
        uint256 floor = 0.0001 ether; // starting price of a token
        uint256 step = 0.0001 ether; // each time increase by this amount in price
        uint256 increment = 10000 ether;

        uint256 cost = (step * (_sold / increment)) + floor;
        return cost;
    }

    function getContributors(address _token) internal view returns (address[] memory, uint256[] memory){
        address[] memory contributors = tokenContributors[_token];
        uint256[] memory contributions = new uint256[](contributors.length);

        for (uint256 i = 0; i < contributors.length; i++) {
            contributions[i] = userEthContributions[_token][contributors[i]];
        }

        return (contributors, contributions);
    }

    function getPriceForTokens(address _token, uint256 _amount) public view returns (uint256) {
        TokenSale storage sale = tokenToSale[_token];
        require(_amount >= 1 ether && _amount <= 10000 ether, "!amount");
        require(sale.isOpen, "!available");

        return getCost(sale.sold) * (_amount / 10 ** 18);
    }

    // function getTokenBySymbol(string memory _symbol) public view returns (address) {
    //     return tokenBySymbol[_symbol];
    // }

    function sendLaunchToRemoteChain(
        uint32 dstEid,
        string calldata _name,
        string calldata _symbol,
        string calldata _metadataURI,
        address _creator,
        bytes memory options
    ) external payable {
        bytes memory payload = abi.encode(_name, _symbol, _metadataURI, _creator);
        _lzSend(dstEid, payload, options, MessagingFee(msg.value, 0), payable(msg.sender));
    }

    function _lzReceive(
        Origin calldata, // not needed for now
        bytes32,         // guid
        bytes calldata payload,
        address,         // executor
        bytes calldata   // extra data
    ) internal override {
        (
            string memory _name, 
            string memory _symbol, 
            string memory _metadataURI, 
            address _creator
        ) = abi.decode(payload, (string, string, string, address));

        _createCrossChainToken(_name, _symbol, _metadataURI, _creator);
    }

    function _createCrossChainToken(
        string memory _name,
        string memory _symbol,
        string memory _metadataURI,
        address _creator
    ) internal {
        Token token = new Token(_creator, _name, _symbol, _metadataURI, 1_000_000 ether, lzEndpoint);
        tokens.push(address(token));
        totalTokens++;
        tokenToSale[address(token)] = TokenSale(address(token), _name, _metadataURI, _creator, 0, 0, true, false);
    }

} ","keccak256":"0x422f9c9f41a24419bf4da9e6049264dd7c36342ad0b932534b30f76023071476","license":"MIT"},"contracts/NativeLiquidityPool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Token.sol";
import "./Factory.sol";

contract NativeLiquidityPool {
    address public factory;
    uint256 public vestingPeriod;

    // Track liquidity providers for each token
    mapping(address => uint256) public liquidity;
    mapping(address => mapping(address => uint256)) public userLiquidity;
    mapping(address => mapping(address => uint256)) public liquidityDepositTime;

    event LiquidityAdded(address indexed tokenAddress, address indexed provider, uint256 tokenAmount, uint256 avaxAmount);
    event LiquidityRemoved(address indexed token, address indexed provider, uint256 tokenAmount, uint256 ethAmount);

    constructor(address _factory) {
        require(_factory != address(0), "Invalid factory");
        factory = _factory;
        vestingPeriod = 604800; // Default vesting period: 1 week (in seconds)
    }

    function setVestingPeriod(uint256 _vestingPeriod) external {
        require(msg.sender == factory, "Only factory can set vesting period");
        vestingPeriod = _vestingPeriod;
    }

    function addLiquidity(address _tokenAddress, uint256 tokenAmount, address[] memory contributors, uint256[] memory contributorAmounts) external payable {
        require(msg.value > 0, "Must provide ETH");
        require(tokenAmount > 0, "Must provide tokens");

        Token token = Token(_tokenAddress);
        require(token.transferFrom(msg.sender, address(this), tokenAmount), "Transfer failed");
        if(msg.sender == factory){
            for (uint256 i = 0; i < contributors.length; i++) {
                userLiquidity[_tokenAddress][contributors[i]] += contributorAmounts[i];
                liquidityDepositTime[_tokenAddress][contributors[i]] = block.timestamp;
            }
        } else {
            userLiquidity[_tokenAddress][msg.sender] += msg.value;
            liquidityDepositTime[_tokenAddress][msg.sender] = block.timestamp;
        }
        liquidity[_tokenAddress] += msg.value;
        emit LiquidityAdded(_tokenAddress, msg.sender, tokenAmount, msg.value);
    }

    function removeLiquidity(address _tokenAddress, uint256 liquidityAmount) external {
        // withdraw eth from the liquidity
        require(userLiquidity[_tokenAddress][msg.sender] >= liquidityAmount, "Insufficient liquidity");
        require(block.timestamp >= liquidityDepositTime[_tokenAddress][msg.sender] + vestingPeriod, "Liquidity is still locked");

        Token token = Token(_tokenAddress);
        uint256 tokenReserve = token.balanceOf(address(this));
        uint256 ethReserve = liquidity[_tokenAddress];

        uint256 ethOut = (liquidityAmount * ethReserve) / liquidity[_tokenAddress]; // uint256 ethOut = liquidityAmount;
        uint256 tokenOut = (liquidityAmount * tokenReserve) / liquidity[_tokenAddress];

        require(ethOut > 0 && tokenOut > 0, "Insufficient withdrawal amount");

        userLiquidity[_tokenAddress][msg.sender] -= liquidityAmount;
        liquidity[_tokenAddress] -= ethOut; // liquidity[_tokenAddress] -= liquidityAmount;

        (bool success, ) = payable(msg.sender).call{value: ethOut}("");
        require(success, "ETH transfer failed");
        require(token.transfer(msg.sender, tokenOut), "Token transfer failed");

        emit LiquidityRemoved(_tokenAddress, msg.sender, tokenOut, ethOut);
    }

    function swapEthForToken(address _tokenAddress) external payable {
        require(msg.value > 0, "Must provide ETH");

        Token token = Token(_tokenAddress);
        uint256 tokenReserve = token.balanceOf(address(this));
        uint256 ethReserve = liquidity[_tokenAddress];

        uint256 tokensOut = (msg.value * tokenReserve)/ (ethReserve + msg.value);
        require(tokensOut > 0, "Insufficient output amount");
        require(token.transfer(msg.sender, tokensOut), "Token transfer failed");

        liquidity[_tokenAddress] += msg.value;
    }

    function swapTokenForEth(address _tokenAddress, uint256 _tokenAmount) external {
        require(_tokenAddress != address(0), "Invalid address");

        Token token = Token(_tokenAddress);
        uint256 tokenReserve = token.balanceOf(address(this));
        uint256 ethReserve = liquidity[_tokenAddress];

        uint256 ethOut = (_tokenAmount * ethReserve) / (tokenReserve + _tokenAmount);
        require(ethOut > 0, "Insufficient output amount");
        require(token.transferFrom(msg.sender, address(this), _tokenAmount), "transfer failed!");

        (bool success, ) = payable(msg.sender).call{value: ethOut}("");
        require(success, "ETH transfer failed");

        liquidity[_tokenAddress] -= ethOut;

    }

    /** 
     * @dev View function to estimate how many tokens will be received for a given ETH amount
     */
    function getEstimatedTokensForEth(address _tokenAddress, uint256 ethAmount) external view returns (uint256) {
        require(ethAmount > 0, "Must provide ETH");

        Token token = Token(_tokenAddress);
        uint256 tokenReserve = token.balanceOf(address(this));
        uint256 ethReserve = liquidity[_tokenAddress];

        if (ethReserve == 0 || tokenReserve == 0) return 0;

        return (ethAmount * tokenReserve) / (ethReserve + ethAmount);
    }

    /** 
     * @dev View function to estimate how much ETH will be received for a given token amount
     */
    function getEstimatedEthForTokens(address _tokenAddress, uint256 tokenAmount) external view returns (uint256) {
        require(tokenAmount > 0, "Must provide tokens");

        Token token = Token(_tokenAddress);
        uint256 tokenReserve = token.balanceOf(address(this));
        uint256 ethReserve = liquidity[_tokenAddress];

        if (ethReserve == 0 || tokenReserve == 0) return 0;

        return (tokenAmount * ethReserve) / (tokenReserve + tokenAmount);
    }

